const showTab=e=>{const t=e.getAttribute("data-tab"),n=document.querySelectorAll(".tabs ul li"),s=document.querySelectorAll(".tab"),o=document.querySelectorAll(".dot");n.forEach(e=>{e.getAttribute("data-tab")===t?e.classList.add("active"):e.classList.remove("active")}),s.forEach(e=>{e.getAttribute("data-tab")===t?e.classList.add("show"):e.classList.remove("show")}),o.forEach(e=>{e.getAttribute("data-tab")===t?e.classList.add("active"):e.classList.remove("active")})}